            var ysum = 0;

            map = new longdo.Map({
            rotate: 90,
            placeholder: document.getElementById('map')
            });
            // ราชภัช สงขลา 100.61286270618439| Lat : 7.169065418767738
            map.location({ lon:100.61286270618439, lat:7.169065418767738 }, true);
            //map.location({ lon:100.66, lat:13.8097 }, true);
            map.zoomRange({ min:16, max:20 });


            //station
            station1 = new longdo.Marker({ lon: 100.613034, lat: 7.167384 },{
            title: 'Custom Marker',
            icon: {
            html: 'อาคาร วิทย์',
            offset: { x:0, y: 0 }
            },
            popup: {
            html: '
popup'
            }
            });
            map.Overlays.add(station1);
            // end marker

            name_bus = document.getElementById("name_bus").value
            longtitude = document.getElementById("longtitude").value
            latitude = document.getElementById("latitude").value
            if(!latitude){
            latitude=13.898285;
            }
            if(!longtitude){
            longtitude=100.537149;
            }
            if(!name_bus){
            name_bus = "";
            }

