function checkborder() {
var location = map.location();

if (location.lon < 100.606597) {
x = 100.606597;
}else if (location.lon > 100.618442) {
x = 100.618442;
}else {
x = location.lon;
}
if (location.lat > 7.179364) {
y = 7.179364;
}else if (location.lat < 7.161225) {
y = 7.161225;
}else {
y = location.lat;
}
console.log("lon : " + x + "| Lat : " + y);
map.location({ lon:x, lat:y }, true);
};

function resetcenter() {
map.location({ lon:100.613034, lat:7.167384 }, true); //university
//map.location({ lon:100.66, lat:13.8097 }, true); //bkk
console.log("reset");
map.zoom(16, true);
}

function fetchLocation() {
// รับค่า name จาก input field

fetch('/get_location', {
method: 'POST',
headers: {
'Content-Type': 'application/json',
},
body: JSON.stringify({
name: name_bus
})
})
.then(response => response.json())
.then(data => {
console.log(data.result);
if (name_bus) {
const locationData = JSON.parse(data.result);

longtitude = locationData.longtitude;
latitude = locationData.latitude;

map.Overlays.remove(car);

car = new longdo.Marker({ lon: longtitude, lat: latitude }, {
title: 'Bus'
});

map.Overlays.add(car);

console.log("Updated position: " + longtitude + ", " + latitude);
} else {
console.log("Not Found Bus name ;(");
}
})
.catch(error => {
console.error('Error fetching location:', error);
});
}

setInterval(fetchLocation, 1000);

map.Event.bind(longdo.EventName.Drop, function() {
checkborder();
});
